	/* 2.2.2 + Magic TODO */
	function stringify_formula(formula, range, cell, supbooks, opts) {
		if(opts !== undefined && opts.biff === 5) return "BIFF5??";
		var _range = range !== undefined ? range : {s:{c:0, r:0}};
		var stack = [], e1, e2, type, c, ixti, nameidx, r;
		if(!formula[0] || !formula[0][0]) return "";
		//console.log("--",cell,formula[0])
		for(var ff = 0, fflen = formula[0].length; ff < fflen; ++ff) {
			var f = formula[0][ff];
			//console.log("++",f, stack)
			switch(f[0]) {
			/* 2.2.2.1 Unary Operator Tokens */
				/* 2.5.198.93 */
				case 'PtgUminus': stack.push("-" + stack.pop()); break;
				/* 2.5.198.95 */
				case 'PtgUplus': stack.push("+" + stack.pop()); break;
				/* 2.5.198.81 */
				case 'PtgPercent': stack.push(stack.pop() + "%"); break;
	
			/* 2.2.2.1 Binary Value Operator Token */
				/* 2.5.198.26 */
				case 'PtgAdd':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"+"+e1);
					break;
				/* 2.5.198.90 */
				case 'PtgSub':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"-"+e1);
					break;
				/* 2.5.198.75 */
				case 'PtgMul':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"*"+e1);
					break;
				/* 2.5.198.45 */
				case 'PtgDiv':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"/"+e1);
					break;
				/* 2.5.198.82 */
				case 'PtgPower':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"^"+e1);
					break;
				/* 2.5.198.43 */
				case 'PtgConcat':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"&"+e1);
					break;
				/* 2.5.198.69 */
				case 'PtgLt':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"<"+e1);
					break;
				/* 2.5.198.68 */
				case 'PtgLe':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"<="+e1);
					break;
				/* 2.5.198.56 */
				case 'PtgEq':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"="+e1);
					break;
				/* 2.5.198.64 */
				case 'PtgGe':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+">="+e1);
					break;
				/* 2.5.198.65 */
				case 'PtgGt':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+">"+e1);
					break;
				/* 2.5.198.78 */
				case 'PtgNe':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+"<>"+e1);
					break;
	
			/* 2.2.2.1 Binary Reference Operator Token */
				/* 2.5.198.67 */
				case 'PtgIsect':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+" "+e1);
					break;
				case 'PtgUnion':
					e1 = stack.pop(); e2 = stack.pop();
					stack.push(e2+","+e1);
					break;
				case 'PtgRange': break;
	
			/* 2.2.2.3 Control Tokens "can be ignored" */
				/* 2.5.198.34 */
				case 'PtgAttrChoose': break;
				/* 2.5.198.35 */
				case 'PtgAttrGoto': break;
				/* 2.5.198.36 */
				case 'PtgAttrIf': break;
	
	
				/* 2.5.198.84 */
				case 'PtgRef':
					type = f[1][0]; c = shift_cell_xls(decode_cell(encode_cell(f[1][1])), _range);
					stack.push(encode_cell(c));
					break;
				/* 2.5.198.88 */
				case 'PtgRefN':
					type = f[1][0]; c = shift_cell_xls(decode_cell(encode_cell(f[1][1])), cell);
					stack.push(encode_cell(c));
					break;
				case 'PtgRef3d': // TODO: lots of stuff
					type = f[1][0]; ixti = f[1][1]; c = shift_cell_xls(f[1][2], _range);
					stack.push(supbooks[1][ixti+1]+"!"+encode_cell(c));
					break;
	
			/* Function Call */
				/* 2.5.198.62 */
				case 'PtgFunc':
				/* 2.5.198.63 */
				case 'PtgFuncVar':
					/* f[1] = [argc, func] */
					var argc = f[1][0], func = f[1][1];
					if(!argc) argc = 0;
					var args = stack.slice(-argc);
					stack.length -= argc;
					if(func === 'User') func = args.shift();
					stack.push(func + "(" + args.join(",") + ")");
					break;
	
				/* 2.5.198.42 */
				case 'PtgBool': stack.push(f[1] ? "TRUE" : "FALSE"); break;
				/* 2.5.198.66 */
				case 'PtgInt': stack.push(f[1]); break;
				/* 2.5.198.79 TODO: precision? */
				case 'PtgNum': stack.push(String(f[1])); break;
				/* 2.5.198.89 */
				case 'PtgStr': stack.push('"' + f[1] + '"'); break;
				/* 2.5.198.57 */
				case 'PtgErr': stack.push(f[1]); break;
				/* 2.5.198.27 TODO: fixed points */
				case 'PtgArea':
					type = f[1][0]; r = shift_range_xls(f[1][1], _range);
					stack.push(encode_range(r));
					break;
				/* 2.5.198.28 */
				case 'PtgArea3d': // TODO: lots of stuff
					type = f[1][0]; ixti = f[1][1]; r = f[1][2];
					stack.push(supbooks[1][ixti+1]+"!"+encode_range(r));
					break;
				/* 2.5.198.41 */
				case 'PtgAttrSum':
					stack.push("SUM(" + stack.pop() + ")");
					break;
	
			/* Expression Prefixes */
				/* 2.5.198.37 */
				case 'PtgAttrSemi': break;
	
				/* 2.5.97.60 TODO: do something different for revisions */
				case 'PtgName':
					/* f[1] = type, 0, nameindex */
					nameidx = f[1][2];
					var lbl = supbooks[0][nameidx];
					var name = lbl.Name;
					if(name in XLSXFutureFunctions) name = XLSXFutureFunctions[name];
					stack.push(name);
					break;
	
				/* 2.5.97.61 TODO: do something different for revisions */
				case 'PtgNameX':
					/* f[1] = type, ixti, nameindex */
					var bookidx = f[1][1]; nameidx = f[1][2]; var externbook;
					/* TODO: Properly handle missing values */
					if(supbooks[bookidx+1]) externbook = supbooks[bookidx+1][nameidx];
					else if(supbooks[bookidx-1]) externbook = supbooks[bookidx-1][nameidx];
					if(!externbook) externbook = {body: "??NAMEX??"};
					stack.push(externbook.body);
					break;
	
			/* 2.2.2.4 Display Tokens */
				/* 2.5.198.80 */
				case 'PtgParen': stack.push('(' + stack.pop() + ')'); break;
	
				/* 2.5.198.86 */
				case 'PtgRefErr': stack.push('#REF!'); break;
	
			/* */
				/* 2.5.198.58 TODO */
				case 'PtgExp':
					c = {c:f[1][1],r:f[1][0]};
					var q = {c: cell.c, r:cell.r};
					if(supbooks.sharedf[encode_cell(c)]) {
						var parsedf = (supbooks.sharedf[encode_cell(c)]);
						stack.push(stringify_formula(parsedf, _range, q, supbooks, opts));
					}
					else {
						var fnd = false;
						for(e1=0;e1!=supbooks.arrayf.length; ++e1) {
							/* TODO: should be something like range_has */
							e2 = supbooks.arrayf[e1];
							if(c.c < e2[0].s.c || c.c > e2[0].e.c) continue;
							if(c.r < e2[0].s.r || c.r > e2[0].e.r) continue;
							stack.push(stringify_formula(e2[1], _range, q, supbooks, opts));
						}
						if(!fnd) stack.push(f[1]);
					}
					break;
	
				/* 2.5.198.32 TODO */
				case 'PtgArray':
					stack.push("{" + f[1].map(mapper).join(";") + "}");
					break;
	
			/* 2.2.2.5 Mem Tokens */
				/* 2.5.198.70 TODO: confirm this is a non-display */
				case 'PtgMemArea':
					//stack.push("(" + f[2].map(encode_range).join(",") + ")");
					break;
	
				/* 2.5.198.38 TODO */
				case 'PtgAttrSpace': break;
	
				/* 2.5.198.92 TODO */
				case 'PtgTbl': break;
	
				/* 2.5.198.71 */
				case 'PtgMemErr': break;
	
				/* 2.5.198.74 */
				case 'PtgMissArg':
					stack.push("");
					break;
	
				/* 2.5.198.29 TODO */
				case 'PtgAreaErr': break;
	
				/* 2.5.198.31 TODO */
				case 'PtgAreaN': stack.push(""); break;
	
				/* 2.5.198.87 TODO */
				case 'PtgRefErr3d': break;
	
				/* 2.5.198.72 TODO */
				case 'PtgMemFunc': break;
	
				default: throw 'Unrecognized Formula Token: ' + f;
			}
			//console.log("::",f, stack)
		}
		//console.log("--",stack);
		return stack[0];
	}
	
